compile-run.test:
Model m1 = new Model();
Model m2 = new Model();
View v = new View();
DeploySupport.deployOnObject(v, m2);
m2.setA(2);
m1.setA(3);			
m2.setA(4);
DeploySupport.undeployFromObject(v, m2);
System.out.println(res.toString());
assertEquals(":model-0:model-2:model-2:model-4", res.toString());

compile-run.code:
        	import org.caesarj.runtime.*;

        	public class Model {
        		int a = 0;
        		public int getA() {
        		   return a;
        		}
        		public void setA(int a) {
        		   this.a = a;
        		}
        		public String toString() {
        			return ":model-" + a;
        		}
			}
			public cclass View implements PerThisDeployable {
				before(Model m): execution(* Model.set*(..)) && this(m) {
					Test.res.append(m.toString());
				}
				after(Model m): execution(* Model.set*(..)) && this(m) {
					Test.res.append(m.toString());
				}
			}
